mapper.select:
SELECT c.*, m.photo_path 
FROM comment c, article a, member m 
WHERE c.article_num = a.article_num AND m.id = c.id AND a.article_num = #{article_num};

mapper.delete:
DELETE FROM COMMENT
WHERE ID = #{id} AND CONTENT =
#{content}

mapper.update:
UPDATE article
SET photo_count = #{count}
WHERE
article_num = #{article_num}

mapper.select:
SELECT COUNT(*)
FROM REACT
WHERE ARTICLE_NUM = #{article_num} AND ID= #{id}

mapper.update:
UPDATE ARTICLE
SET REACT_COUNT = REACT_COUNT+1
WHERE ARTICLE_NUM = #{article_num}

UPDATE REACT
SET TYPE_NUM = ${expression}, REACT_TIME = now()
WHERE ARTICLE_NUM = #{article_num} AND ID = #{id}

mapper.insert:
INSERT INTO REACT(ARTICLE_NUM, ID, TYPE_NUM, REACT_TIME)
VALUES(#{article_num}, #{id}, ${expression}, now())
ON DUPLICATE KEY UPDATE ID=#{id} AND ID=#{id},TYPE_NUM=${expression};
